for(ncali in unique(almond_master$ncal)){
for(c.l in unique(almond_master$cult_loc)){ print(c.l)
#c.l <- unique(training_df$cult_loc)[1]
#skip the calibration if there is already an entry in the table for(i in 1:max(almond_master$r)){
#i<- 1
sub <- almond_master %>% filter(r == i, cult_loc == c.l, ncal == ncali, split == 'Calibration') %>% arrange(year) %>% mutate(loc.year = paste(location, year, sep ='.'))
cult <- c.l %>% strsplit('-') %>% purrr::pluck(1) %>% purrr::pluck(1)
loc <- c.l %>% strsplit('-') %>% purrr::pluck(1) %>% purrr::pluck(2)
if(file.exists(fname)){ res <- read.csv(fname)
if(any(res$repetition == i & res$cultivar == cult & res$location == loc & res$n_cal == 'scarce')) next() }
#realized that the default values of PhenoFlex paper are not default values of dynamic model #taken from phenoflex function #mistake in the E0 parameter #par_fixed <- c(25, 3372.8, 9900.3, 6319.5, 5.939917e+13, 4,36, 4, 1.6)
#use standard parameters of dynamic model by Erez 1990 (and not default in PhenoFlex!!!) # Tu E0 E1 A0 A1 Tf Tc Tb slope par_fixed <- c(25, 4153.5, 12888.8, 139500, 2.567e+18, 4, 36, 4, 1.6)
#took values of ferragnes repetition 1 from adamedor as starting point x_0 <- c(21.3952307, 404.5477002, 0.8639453) x_U <- c(50, 700, 1.2) x_L <- c(5, 100, 0.1)
#limits for the inequality constraints # #gdh parameters #q10 for E0 and E1 #default parameters of dynamic model violate the q10 assumption, so just set large tolerance range c_L <- c( 0, 0, 0, 0, 0) c_U <- c(Inf, Inf, Inf, Inf, Inf)
problem<-list(f="evalfun", x_0 = x_0, x_L = x_L, x_U = x_U, c_L = c_L, c_U = c_U)
#change iterations #you can control the maximum time of running or max number of iterations #options for fitter opts<-list(maxeval = 5000, #maxtime = 60 * 30, local_solver = 'DHC', local_bestx = 1, inter_save = 0, plot = 1)
set.seed(123456789)
res_list <- LarsChill::custom_essr(problem = problem, opts, modelfn = evalpheno::custom_PhenoFlex_GDHwrapper, bloomJDays = sub$yday, SeasonList = seasonlist[sub$loc.year], par_fixed = par_fixed)
#save outcome in a table, append the table data.frame(repetition = i, cultivar = unique(sub$cultivar), location = locs, yc = res_list$xbest[1], zc = res_list$xbest[2], s1 = res_list$xbest[3], Tu = res_list$xbest[4], theta_star = theta_star, theta_c = res_list$xbest[5], tau = res_list$xbest[6], pie_c = res_list$xbest[7], Tf = res_list$xbest[8], Tc = Tc, Tb = res_list$xbest[9], slope = res_list$xbest[10], fit = 'combined', n_cal = ncali) %>% write.table(file = fname, append = TRUE, row.names = FALSE, sep = ',', col.names=!file.exists(fname)) } } }

temp <- seq(-5, 40, by = 0.1)
#make data.fame with temperature response plots response_df <- purrr::map(1:nrow(par_all), function(i){
par_all[i, LarsChill::phenoflex_parnames_old] %>% unlist() %>% LarsChill::get_temp_response_df(temp_values = temp) %>% mutate(species = par_all$species[i], cultivar = par_all$cultivar[i], fit = par_all$fit[i], n_cal = par_all$n_cal[i]) %>% return()
}, .progress = TRUE) %>% bind_rows()
write.csv(response_df, file = 'data/response_df.csv', row.names = FALSE)

#vizualize also the transition parameters. yc and s1 get_share_heat <- function(y, yc, s1){ sr <- exp(s1 * yc * ((y - yc)/y)) return((sr) / (1+sr)) }
s1 <- 1 yc <- 40 y <- 40
curve_df <- purrr::map(1:nrow(par_all), function(i){ curve <- get_share_heat(0:100, par_all[i,'yc'], par_all[i,'s1']) return(data.frame(y = 0:100, yc = par_all[i,'yc'], s1 = par_all[i,'s1'], share_heat = curve, species = par_all$species[i], cultivar = par_all$cultivar[i], fit = par_all$fit[i], n_cal = par_all$n_cal[i]))
}) curve_df <- do.call('rbind', curve_df)
#calculate the yintercept for the line around the infliction point vector_length <- 0.5 step_right <- 1
curve_df %>% mutate(fit_plot = factor(fit, levels = c('baseline', 'single', 'combined'), labels = c('Baseline Model', 'Cultivar-Fit', 'Combined-Fit'))) %>% mutate(id = paste(species, cultivar, fit, n_cal, sep = '_'), a = 0.5 - (s1*yc), x_start = yc-step_right, x_end = yc+step_right, y_start = a + s1 *x_start, y_end = a + s1 * x_end) %>% mutate(x_vec = x_start - x_end, y_vec = y_start - y_end, length = sqrt(x_vec^2 + y_vec^2), x_einheit = x_vec / length, y_einheit = y_vec / length, x_plot_start = yc - (x_einheit*vector_length), x_plot_end = yc + (x_einheit*vector_length), y_plot_start = 0.5 - (y_einheit*vector_length), y_plot_end = 0.5 + (y_einheit * vector_length)) %>% filter(cultivar == 'Schneiders', fit == 'combined', n_cal == 'full') %>% ggplot(aes(x = y, y = share_heat, group = id)) + geom_line(show.legend = FALSE) + geom_segment(aes(y = 0.5, yend = 0, x = yc, xend = yc), col = 'red', linetype = 'dashed') + geom_point(aes(y = 0.5, x = yc), col = 'red', shape = 4, size = 1) + geom_segment(aes(y = y_plot_start, yend = y_plot_end, x = x_plot_start, xend = x_plot_end), col = 'blue') + theme_bw(base_size = 15) + ylab('Share of potential heat that gets accumulated') + ylab('Amount of chill (y) accumulated')
